def clasificateCluster(cluster_number: Int, data : DataFrame) : String = {
    //Defino UDF para tomar 10 tweets por palabra y sus 10 sentimientos
    val take10tweets_ = udf((xs: Seq[String]) => xs.take(10))
    val take10Sentiments_ = udf((xs: Seq[Double]) => xs.take(10))

    //pre filter tweets by cluster Number
    val predictions_tweets = nbModel.transform(data.filter($"clusterNr" === cluster_number))

    //Explode filtered words from tweet
    val wordsExploded = predictions_tweets.select($"text", explode($"filtered").as("wordsExploded"), $"prediction", $"clusterNr")


    //calculate the average of classification from word
    val wordsClusterAvgCount = wordsExploded.groupBy("wordsExploded","clusterNr").agg(avg("prediction").as("avgP"), count("wordsExploded").as("wordCount"), take10tweets_(collect_list($"text")).as("TweetLst"), take10Sentiments_(collect_list($"prediction")).as("sentimentLst")).filter($"wordCount" > 50)

    // if avgP is >= means that the word ocurrs more often in positives tweets then i set 1 (Green) otherwise 0 (Red)
    val greenWords = wordsClusterAvgCount.filter($"avgP" >= 0.5).withColumn("sentiment", lit(1))
    val redWords = wordsClusterAvgCount.filter($"avgP" < 0.5).withColumn("sentiment", lit(0))
    val textClusterCountColor = greenWords.union(redWords)

    //build json
    val dfToJson = (s"""{"children":[${textClusterCountColor.toJSON.collect.mkString(", ")}]}""")
    return dfToJson
}

