print(s"""%html 
<div id="div-tweet"></div>
<div id="bubble-chart"></div> 

<script>

var diameter = 1000, // changed size
    format = d3.format(",d"),
    colors = ["#FF0000","#00FF00"];

var bubble = d3.layout.pack()
    .sort(null)
    .size([diameter, diameter])
    .padding(1.5);

//div for bubble chart
var svg = d3.select("#bubble-chart").append("svg") //changed where to draw, changed to draw to my div
    .attr("width", diameter)
    .attr("height", diameter)
    .attr("class", "bubble");

//set div for tweet list with some arguments
var tweetDiv = d3.select("#div-tweet")
    .attr("width", diameter)
    .attr("height", diameter)

var plot = function(root) {

  var node = svg.selectAll(".node")
      .data(bubble.nodes(classes(root))
      .filter(function(d) { return !d.children; }))
    .enter().append("g")
      .attr("class", "node")
      .attr("transform", function(d) { return "translate(" + d.x + "," + d.y + ")"; })
      .on("click", show_tweets)
      .on("mouseover", function(){d3.select(this).style("fill", "white");})
      .on("mouseout", function(){d3.select(this).style("fill", "black");});

  node.append("title")
      .text(function(d) { return d.className + ": " + format(d.value); });

  node.append("circle")
      .attr("r", function(d) { return d.r; })
      .style("fill", function(d) { return colors[d.sentiment]; });

  node.append("text")
      .attr("dy", ".3em")
      .style("text-anchor", "middle")
      .text(function(d) { return d.className.substring(0, d.r / 2); });

}

function show_tweets(d) {
    var tweetList, sentimentList, len;
    tweetList = d.tweetLst;
    sentimentList = d.sentimentLst;
    len = tweetList.length;

    text = "<table><tr><th>Tweet</th><th>Sentiment</th>";    
    for (i = 0; i < len; i++) {
        if(sentimentList[i] == 0){
            text += "<tr><td style=color:red;>" + tweetList[i] + "</td>" + "<td style=color:red;>" + 'Negative' + "</td></tr>" ;    
        }else{
            text += "<tr><td style=color:green;>" + tweetList[i] + "</td>" + "<td style=color:green;>" + 'Positive' + "</td></tr>"
        }
    }
    text += "</table>"
    document.getElementById("div-tweet").innerHTML = text;
}

// Returns a flattened hierarchy containing all leaf nodes under the root.
function classes(root) {
  var classes = [];

  function recurse(name, node) {
    if (node.children) node.children.forEach(function(child) { recurse(node.name, child); });
    else classes.push({packageName: name, className: node.wordsExploded, value: node.wordCount, cluster: node.clusterNr, tweetLst: node.TweetLst, sentiment:node.sentiment, sentimentLst: node.sentimentLst});
  }


  recurse(null, root);
  return {children: classes};
}

plot(${clusterDataMovie}) // inject json from scala

d3.select(self.frameElement).style("height", diameter + "px");

</script>

""")
